INSERT INTO `sm_goods` VALUES(855, 147, '1SKB19-4U', 'osc855', 'SKB Standard 4U Effects Rack', '+', 555, 127, '', 0, 0.000, 2220, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p>SKB 194U是一款容积为4个标准机柜空间大小的设备箱。</p>\r\n<p>SKB 公司的 19 英寸设备箱以期坚固耐用的特征赢得了业界同仁的广泛认可，该设备箱采用高分子聚乙烯材质制成，其重量只有原木制设备箱重量的一半，但坚韧度远胜从前。该设备箱的前后面板带有密封垫，可以保护您的设备免收湿气和灰尘的侵害。<br />\r\n&nbsp;</p>', 'images/200912/1260816508378461853.jpg', 'images/200912/1260816508730411124.jpg', 'images/200912/1260816508524991650.jpg', 1, '', 1, 1, 0, 1260816508, 0, 0, 0, 0, 0, 0, 0, 0, 1284513327, 1, '', -1, '', '', 0, 0, '');
INSERT INTO `sm_goods` VALUES(856, 147, 'SKB1615 ', 'osc856', 'SKB SKB-1615 小型调音台机箱', '+', 468, 127, '', 0, 0.000, 0, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p>该产品是一款小型的混音器ATA箱。SKB1615 是一款用于放置小型混音器的 ATA 箱。</p>\r\n<p><strong>特征： </strong><br />\r\n14.5 英寸 x 13.75 英寸 x 6.25 英寸。 <br />\r\n结实耐用的聚乙烯方形泡沫内衬。 <br />\r\n外部采用脊状设计，可起到缓冲保护作用。 <br />\r\n符合 ATA-300 CAT 1 规格。 <br />\r\n&nbsp;</p>', 'images/200912/1260816730660074270.jpg', 'images/200912/1260816730343713919.jpg', 'images/200912/1260816730452181172.jpg', 1, '', 1, 1, 0, 1260816730, 0, 0, 0, 0, 0, 0, 0, 0, 1260818665, 1, '', -1, '', '', 0, 0, '');
INSERT INTO `sm_goods` VALUES(857, 147, 'SKB-4214W', 'osc857', 'SKB SKB-4214W 61键键盘飞行箱', '+', 616, 127, '', 0, 0.000, 0, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p><font face="Verdana">该产品是一款用于61键键盘的箱子，设置了结实耐用的螺旋插销，橡胶缓冲器和泡沫内衬。</font></p>\r\n<p align="center"><font face="Verdana"><img alt="" border="0" src="http://www.audioplaza.com.cn/ewebeditor/UploadFile/200812270426728.jpg" /></font></p>\r\n<p>&nbsp;　</p>\r\n<p><font face="Verdana">SKB SKB5014W 是一款用于放置 61 键键盘的箱子， 该产品设置了轮子，适合于任何 42 英寸长， 14 英寸深的键盘.</font></p>\r\n<p><font face="Verdana">它按照 ATS 标准制作而成，角边均设置了橡胶缓冲器，拥有 4 个结实的螺旋插销以防止意外打开，另外它的各个边还设置了完全的泡沫保护，这些都为您设备的安全提供了可靠的保障。 　　</font></p>\r\n<p><font face="Verdana">该产品不适用于 Yamaha Motif 系列 , ASR10, Korg Trinity Pro, Ensoniq等设备</font></p>', 'images/200912/1260817008536233944.jpg', 'images/200912/1260817007550542663.jpg', 'images/200912/1260817007699481866.jpg', 1, '', 1, 1, 0, 1260817008, 0, 0, 0, 0, 0, 0, 0, 0, 1260818649, 1, '', -1, '', '', 0, 0, '');
