INSERT INTO `sm_goods` VALUES(859, 147, 'SKB19-R1006', 'osc859', 'SKB SKB19-R1006 机箱', '+', 440, 127, '', 0, 0.000, 0, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p>SKB SKB-3026 是一款几乎适于放置所有调音台的机柜以及一些采样器等设备的机箱，它采用了蝴蝶状插销、外部拐角缓冲器，很好的保证了设备的安全性，另外其顶部还可以完全移动。该产品采用了坚固的聚乙烯结构设计，结实耐用，另外它的铝质帷幔和闭合处的橡胶垫圈，还有效的起到了防尘、防潮的作用。</p>\r\n<p align="center"><img alt="" border="0" src="http://www.audioplaza.com.cn/ewebeditor/UploadFile/2008122723464643.jpg" /></p>\r\n<p class="dtlt"><strong>产品详细参数:</strong></p>\r\n<p><span id="labInfo">独特的拐角夹板设计可将您的<span class="botlink1">调音台</span>牢牢地固定了下来。 <br />\r\n内部采用方格状的泡沫可根据<span class="botlink1">调音台</span>的形状自由调整。 <br />\r\n顶部可完全去除。 <br />\r\n内部尺寸：宽 29 3/4 英寸，长 26 1/4 英寸，高 8 英寸</span></p>', 'images/200912/1260817311959429627.jpg', 'images/200912/1260817311264171276.jpg', 'images/200912/1260817311182785177.jpg', 1, '', 1, 1, 0, 1260817311, 0, 0, 0, 0, 0, 0, 0, 0, 1260818620, 1, '', -1, '', '', 0, 0, '');
INSERT INTO `sm_goods` VALUES(860, 147, 'SKB-19-P12', 'osc860', 'SKB SKB-19-P12 调音台机箱', '+', 481, 127, '', 0, 0.000, 0, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p><span id="labIntro">SKB19P12 是一款具有12 机柜大小的混音台便携箱，该便携箱采用了自动跳起式的设计。 　</span></p>\r\n<p><span>该便携箱适用于所有 12 通道的调音台。</span></p>', 'images/200912/1260817438927728208.jpg', 'images/200912/1260817437744033133.jpg', 'images/200912/1260817437050070915.jpg', 1, '', 1, 1, 0, 1260817438, 0, 0, 0, 0, 0, 0, 0, 0, 1260818603, 1, '', -1, '', '', 0, 0, '');
