INSERT INTO `sm_goods` VALUES(862, 147, 'SKB-1200', 'osc862', 'SKB SKB-1200 话筒便携箱', '+', 451, 127, '', 0, 0.000, 0, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p><font face="Verdana">KB-1200 是一款可以放置 12 支麦克风的便携箱，且其具有足够的空间供您放置电缆等附件 。 </font></p>\r\n<p><font face="Verdana">SKB SKB-1200 话筒便携箱总共可以放置 12 支麦克风，在设备箱的盖子上专门设计了可以放置附件的口袋，且其还可以提供宽广的空间供您放置电缆。 　　</font></p>\r\n<p><font face="Verdana">SKB SKB-1200 话筒便携箱内部采用了泡沫塑料的设计，可以更好的保护您的麦克风。 　　此外，该便携箱的侧面还设有把手，方便了您的搬运。</font></p>', 'images/200912/1260817710667072494.jpg', 'images/200912/1260817710504923811.jpg', 'images/200912/1260817710830200665.jpg', 1, '', 1, 1, 0, 1260817710, 0, 0, 0, 1, 0, 0, 0, 0, 1260852025, 1, '', -1, '', '', 0, 0, '');
INSERT INTO `sm_goods` VALUES(863, 147, 'RotoGigRig', 'osc863', 'SKB Roto Gig Rig 设备便携箱', '+', 446, 127, '', 0, 0.000, 0, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p><font face="Verdana">SKB Gig Rig是一款设备便携箱，其顶部为10个机柜宽度，正面为10个机柜高度。</font></p>\r\n<p><font face="Verdana">SKB Gig Rig 便携箱是 SKB 推出的一款产品，其顶盖、前面板和后面板用户可以根据需要进行分拆，在装载完设备后重新进行组装。 </font></p>\r\n<p><font face="Verdana">SKB Gig Rig 便携箱可以在运输和演出中防止您的设备不被刮划和撞击，很好的保护您的设备。 </font></p>', 'images/200912/1260817807653893099.jpg', 'images/200912/1260817807343801700.jpg', 'images/200912/1260817807040209525.jpg', 1, '', 1, 1, 0, 1260817807, 0, 0, 0, 1, 0, 0, 0, 0, 1260852027, 1, '', -1, '', '', 0, 0, '');
