INSERT INTO `sm_goods` VALUES(1113, 164, 'osc001113', 'osc1113', 'SKB 1SKB19-R1208 Gig Rig 飞行箱', '+', 240, 127, '', 0, 0.000, 0, 0, 0, 0, 0, 0, 0.00, 0, 0, 1, '', '', '<p><font face="Verdana">Gig Rig<br />\r\n型号：1SKB19-R1208<br />\r\n类型：Gig Rig</font></p>\r\n<p style="text-align: center"><font face="Verdana"><img width="360" height="360" alt="" src="/images/upload/Image/1(135).jpg" /></font></p>\r\n<p style="text-align: center"><font face="Verdana"><img width="360" height="360" alt="" src="/images/upload/Image/1(136).jpg" /></font></p>\r\n<p style="text-align: center"><font face="Verdana"><img width="360" height="360" alt="" src="/images/upload/Image/1(137).jpg" /></font></p>\r\n<p style="text-align: center"><font face="Verdana"><img width="360" height="360" alt="" src="/images/upload/Image/1(138).jpg" /></font></p>\r\n<p style="text-align: center"><font face="Verdana"><img width="360" height="360" alt="" src="/images/upload/Image/1(139).jpg" /></font></p>\r\n<p style="text-align: center"><font face="Verdana"><img width="360" height="360" alt="" src="/images/upload/Image/1(140).jpg" /></font></p>\r\n<p style="text-align: center"><font face="Verdana"><img width="360" height="258" alt="" src="/images/upload/Image/1(141).jpg" /></font></p>\r\n<p style="text-align: left"><font face="Verdana"><font face="Verdana">&nbsp;skb的Gig Rig系列为混频器/机架箱设定的标准已经超过了15年。第四代Gig Rig将标准提高到新的高度。 1SKB19- R1208Gig Rig，在1SKB19- R1406Gig Rig强大设计的基础上，提供了超过之前Gig Rig的更多功能性。</font> </font></p>\r\n<p><font face="Verdana">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 线性低密度聚乙烯（LLDPE）滚塑成型，1SKB19- R1208Gig Rig的机身构造是非常耐用的。 7?角度顶部的钢轨，在电缆连接的背面提供了额外的12U机架空间。盖子同样空间充裕。连接电缆之后，??可选1SKB AV8伸缩式货架空间。前8U钢轨将给放大器或外部设备提供足够的空间。后机架导轨能安装额外的存储和安装。</font></p>\r\n<p><font face="Verdana">&nbsp;&nbsp;&nbsp;&nbsp; 在同样的方式下，强大的Gig Rig盖子还可以被用来作为1SKB19- R1208 Gig Rig的底座。通过放置在底部的轮子并锁定在上面的Gig Rig，混频器的高度完美的表现。 1SKB R1208 Gig Rig包括两个注塑超长手柄，能够在运输的时候自由升降。四个重负载的脚轮完成了新的Gig Rig。</font></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><font face="Verdana">&nbsp;--可容纳较长的混频器<br />\r\n&nbsp;--更大的立式混音器深度<br />\r\n&nbsp;--可容纳AV- 8的伸缩式货架<br />\r\n&nbsp;--坚不可摧的注塑成型侧边把手<br />\r\n&nbsp;--锁定闩锁<br />\r\n&nbsp;--脚轮锁定<br />\r\n&nbsp;--可添加R19061SKB额外的机架单位</font></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><font face="Verdana">&nbsp;机架空间单位共有&nbsp; 28U<br />\r\n&nbsp;机架顶部空间单位&nbsp; 12U<br />\r\n&nbsp;机架底部空间单位&nbsp; 16U<br />\r\n</font></p>', 'images/201111/1320344813388970129.jpg', 'images/201111/1320344813359316767.jpg', 'images/201111/1320344813464512897.jpg', 1, '', 1, 1, 0, 1320344813, 0, 0, 0, 0, 0, 0, 0, 0, 1320344813, 1, '', -1, '', '', 0, 0, '');
